select

from tb_material_in
WHERE  yn = 0
AND material_id = #{id}

  AND DATE_FORMAT(gmt_create,'%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}

limit #{pager.start},#{pager.size}

SELECT COUNT(*) FROM tb_material_in
WHERE yn=0
AND material_id = #{id}

  AND DATE_FORMAT(gmt_create,'%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}

SELECT SUM(material_in_num) FROM tb_material_in
WHERE yn=0
AND material_id = #{id}

  AND DATE_FORMAT(gmt_create,'%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}

select

from tb_material_in
WHERE  yn = 0

  AND DATE_FORMAT(gmt_create,'%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}

