mapper.select:
select

from tb_product_material
WHERE  yn = 0
AND product_id = #{id}

  AND DATE_FORMAT(gmt_create,'%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}

limit #{pager.start},#{pager.size}

SELECT COUNT(*) FROM tb_product_material
WHERE yn=0
AND product_id = #{id}

  AND DATE_FORMAT(gmt_create,'%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}

SELECT SUM(product_num) FROM tb_product_material
WHERE yn=0
AND product_id = #{id}

  AND DATE_FORMAT(gmt_create,'%Y-%m-%d') BETWEEN #{startTime} AND #{endTime}
